function prePageFun() {
    currpage -= 1;
    pageSel();
}

function nextPageFun() {
    currpage += 1;
    pageSel();
}

function firstPageFun() {
    currpage = 0;
    pageSel();
}

function lastPageFun() {
    currpage = totalPage;
    pageSel();
}

function changePageSize(){
  pageSel();
}


function pageSel() {
     require(['jquery'], function ($) {
       var selection = $('.ant-select-selection-selected-value');
       if (selection.length > 0) {
       var proId = selection[0].dataset.projectid;
       var orgId = selection[0].dataset.orgid;
        var size = $('#selPageSize option:selected').val();
       var searchUrl = urlPrex+"/agile/v1/projects/"+proId+"/issues/include_sub?organizationId="+orgId+"&page="+currpage+"&size="+size;
       $.ajax({
        headers: {
            'Authorization': token
        },
        type: "POST",
        contentType: "application/json",
        dataType:"json",
        url: searchUrl,
        data:"{\"searchArgs\":{\"summary\":\""+$(" #name ").val()+"\"}}",
        success: function (data) {
           var content = data.content;
          var tbodyHtml = "";
          if(content.length > 0){
            totalPage = data.totalPages - 1;
            if (data.number == 0) {
                $("#firstPage ").attr("disabled", "disabled");
                $("#prePage ").attr("disabled", "disabled");
            } else {
                $("#firstPage ").removeAttr('disabled');
                $("#prePage ").removeAttr('disabled');
            }
            if (data.number == data.totalPages - 1) {
                $("#nextPage ").attr("disabled", "disabled");
                $("#lastPage ").attr("disabled", "disabled");
            } else {
                $("#nextPage ").removeAttr('disabled');
                $("#lastPage ").removeAttr('disabled');
            }
            var typeCode = "";
            var typeIcon;
            for (var i = 0; i < content.length; i++) {
                typeCode = content[i].typeCode;
                if (typeCode == "bug") {
                    typeIcon = "icon-bug_report";
                } else if (typeCode == "task") {
                    typeIcon = "icon-assignment"
                }else if(typeCode == "issue_epic"){
                    typeIcon = "icon-priority";
                  } else {
                    typeIcon = "icon-turned_in";
                }
                tbodyHtml += "<tr style=\"background-color: transparent;cursor: pointer;\" onclick='selectedTr(this)'   data-statusName=\""+content[i].statusMapDTO.name+"\"  data-summary=\""+content[i].summary+"\" data-statusType=\""+content[i].statusMapDTO.type+"\" data-typeCode=\"" + typeCode + "\"  data-issueNum=\"" + content[i].issueNum + "\" data-issueId=\"" + content[i].issueId+"\" data-projectId=\"" + content[i].projectId+"\" data-orgId=\"" + orgId +"\" data-url=\""+fontUrl+"/#/agile/issue?type=project&id=" + content[i].projectId +"&organizationId="+ orgId +"&paramName=" + content[i].issueNum + "&paramIssueId=" + content[i].issueId + "\"><td><div class=\"icon\" ><i class=\"icon td-icon " + typeIcon + " td-summary \" ></i></div>" + content[i].issueNum + "</td><td class=\"td-summary\">" + content[i].summary + "</td></tr>"
            }
          }else{
            tbodyHtml += " <tr style=\"background-color: transparent;\"><td>没有数据</td></tr>";
          }
            $('#issues_summary').html(tbodyHtml);
        },
        error: function (err) {
           require(['toastMsg'],function(toastMsg){
                toastMsg.toast('获取issue失败！');
         })
        }
    })
     }
     })
}
function enterSearch(){//回车搜索
    var event=window.event?window.event:event;   
    if(event.keyCode==13){
       btnSearch();
   }
}

function btnSearch() {
  currpage = 0;
  pageSel();
}

function selectedTr(a){
  require(['jquery'], function($) {
    $(a).parent().find("tr.focus").toggleClass("focus");//取消原先选中行
    $(a).toggleClass("focus");//设定当前行为选中行
})
}



function txtfocus(){
  CKEDITOR.dialog.getCurrent().getButton("ok").disable(); 
}

function txtblur(){
  CKEDITOR.dialog.getCurrent().getButton("ok").enable();
}



