/*点击下拉框显示选项列表*/
function selTypeChange() {
  require(['jquery'], function($){
    var selOption = document.getElementById('sel-option');
    if(selOption) {
      selOption.toggle();
    }
  });
};
/*点击选项将内容替换到下拉框中并收起下拉选项*/
 function optionSel(e) {
     var selOption = document.getElementById('sel-option');
     var selShow = document.getElementById('sel-show');
     selShow.innerHTML =e.innerHTML;;
     selOption.style.display = 'none';
     flag = false;
};

function createSsummaryBlur(){
   require(['jquery'], function($) {
       var summary = $.trim($('#create-summary').val()); 
    if(summary.length > 0){
       $('#create-summary-required').addClass("form-required-hide")
     }else{
       $('#create-summary-required').removeClass("form-required-hide");
     }
   })
}

function tcreateSsummaryFocus(){}

function getCookie(c_name)
{
if (document.cookie.length>0)
  {
  c_start=document.cookie.indexOf(c_name + "=")
  if (c_start!=-1)
    { 
    c_start=c_start + c_name.length+1 
    c_end=document.cookie.indexOf(";",c_start)
    if (c_end==-1) c_end=document.cookie.length
    return unescape(document.cookie.substring(c_start,c_end))
    } 
  }
return ""
}

function insertToEditor(editor,data){
  var dataIcon;
  var datacolor;
  var statusBackColor;
  
  switch (data.typeCode){
    case "bug":
      dataIcon = "icon-bug_report";
      datacolor = "rgb(244, 67, 54)";
      break;
    case "task":
      dataIcon = "icon-assignment"
      datacolor = "rgb(77, 144, 254)";
      break;
    case "issue_epic":
      dataIcon = "icon-priority";
      datacolor = "rgb(116, 59, 231);"
      break;
    case "story":
      dataIcon = "icon-turned_in";
      datacolor = "rgb(0, 191, 165)";
      break;
   
  }
  
  switch (data.statusType) {
      case "todo" :
        statusBackColor = 'rgb(255, 177, 0)';
        break;
      case "doing" :
        statusBackColor = 'rgb(77, 144, 254)';
        break;
      case "done" :
        statusBackColor = 'rgb(0, 191, 165)';
        break;
   }
   if(data.issueNum != null){
     var url = fontUrl+"/#/agile/issue?type=project&id=" +data.projectId +"&organizationId="+ data.organizationId +"&paramName=" + data.issueNum + "&name="+data.projectText+"&paramIssueId=" + data.issueId;
     var htmlTmp = "<a href=\""+url+"\" data-projectid=\""+data.projectId +"\" data-organizationid=\""+data.organizationId +"\" data-issueid=\""+data.issueId +"\" class=\"icon "+dataIcon+"\" target=\"view_window\" style=\"font-size: initial;color:"+datacolor+";\">"+data.issueNum+"<span>";
     htmlTmp += "<span class=\"c7n-view-issue\" style=\"color:#000000\">&nbsp;- "+data.summary+"&nbsp;</span><span class=\"c7n-statusTag c7n-view-issue\" style=\"background-color:"+statusBackColor+"\">";
     htmlTmp += data.statusName+"</span>&nbsp;</a>";
     editor.insertHtml(htmlTmp);
     return true;
   }else{
     require(['toastMsg'],function(toastMsg){
         toastMsg.toast('没有选择插入的数据！');
       })
     return false;
   }
  
}


function selectfilter(e){
  require(['jquery'], function($) { 
  if(e.keyCode==13){
  var search_val = $.trim($("#ant-select-dropdown-filter-input").val());
  for (var i = items.length - 1; i >= 0; i--) {
     var val = items[i].innerText;
     if (val.toLowerCase().indexOf(search_val.toLowerCase()) != -1) {
       items[i].style.display = 'block';
     } else {
       items[i].style.display = 'none';
     }
  }
        }
 });
}

function createfilter(e){
  require(['jquery'], function($) { 
  if(e.keyCode == 13){
  var search_val = $.trim($("#create-ant-select-dropdown-filter-input").val());
  for (var i = itemss.length - 1; i >= 0; i--) {
     var val = itemss[i].innerText;
     if (val.toLowerCase().indexOf(search_val.toLowerCase()) != -1) {
       itemss[i].style.display = 'block';
     } else {
       itemss[i].style.display = 'none';
     }
  }
 }
 });
}


function selectProjectHeader(){
   require(['jquery'], function($) { 
     var isHidden = $("#project-select-dropdown").hasClass("ant-select-dropdown-hidden");
     if (isHidden) {
       $("#project-select-dropdown").removeClass("ant-select-dropdown-hidden");
       $("#ant-select-dropdown-filter-input").val("");
       $(".ant-select-dropdown-menu-item").each(function(){
          $(this).css('display','block'); 
       });
     } else {
       $("#project-select-dropdown").addClass("ant-select-dropdown-hidden");
     }
   });
}

function createSelectProjectHeader(){
   require(['jquery'], function($) { 
     var isHidden = $("#create-project-select-dropdown").hasClass("ant-select-dropdown-hidden");
     if (isHidden) {
       $("#create-project-select-dropdown").removeClass("ant-select-dropdown-hidden");
       $("#create-ant-select-dropdown-filter-input").val("");
       $(".ant-create-dropdown-menu-item").each(function(){
          $(this).css('display','block'); 
       });
     } else {
       $("#create-project-select-dropdown").addClass("ant-select-dropdown-hidden");
     }
   });
}

function li_select_method(e){
  var val = '<div class="ant-select-selection-selected-value" data-orgid="'+e.getAttribute("data-orgid")+'" data-projectid="'+e.getAttribute("data-projectid") +'" style="opacity: 1;cursor: pointer;" title="'+e.innerText+'">'+e.innerText+'</div>';
  document.getElementById("selProject").innerHTML = val;
  selectProjectHeader();
  pageSel();
}

function li_create_method(e){
  var val = '<div class="ant-create-selection-selected-value" data-orgid="'+e.getAttribute("data-orgid")+'" data-projectid="'+e.getAttribute("data-projectid") +'" style="opacity: 1;cursor: pointer;" title="'+e.innerText+'">'+e.innerText+'</div>';
  document.getElementById("create-selProject").innerHTML = val;
  createSelectProjectHeader();
}

//全局监听点击事件，用于隐藏空间弹出框和用户信息弹出框
document.addEventListener("click", function (e) {
   var projectSelectDropdown = document.getElementById("project-select-dropdown");
   var selectProjectHeader = document.getElementById("selectProjectHeader");
   var ckissue = document.getElementsByClassName("cke_button__ck-issue");
   if(projectSelectDropdown && selectProjectHeader && ckissue.length > 0) {
     if(!projectSelectDropdown.contains(e.target) && !selectProjectHeader.contains(e.target)  && !ckissue[0].contains(e.target)) {
       require(['jquery'], function($) { 
       $("#project-select-dropdown").addClass("ant-select-dropdown-hidden");
       });
     }
   }
  
   var createProjectSelectDropdown = document.getElementById("create-project-select-dropdown");
   var createSelectProjectHeader = document.getElementById("createSelectProjectHeader");
   var ckissue = document.getElementsByClassName("cke_button__ck-issue");
   if(createProjectSelectDropdown && createSelectProjectHeader && ckissue.length > 0) {
     if(!createProjectSelectDropdown.contains(e.target) && !createSelectProjectHeader.contains(e.target)  && !ckissue[0].contains(e.target)) {
       require(['jquery'], function($) { 
       $("#create-project-select-dropdown").addClass("ant-select-dropdown-hidden");
       });
     }
   }
  
})

