<script type="application/javascript">
    function nameOnInput(a) {
        require(['jquery'], function ($) {
            var name = $.trim($('#appName').val());
            if(name.length > 0){
                $('#btnSave').removeAttr("disabled");
                $("#appName-validate").removeClass("validate-span");
                $("#appName").removeClass("validate-input");
            }else {
                $('#btnSave').attr("disabled",true);
                $("#appName-validate").attr("class", "validate-span");
                $("#appName").addClass("validate-input");
            }
        })
    }
</script>

