function getParams(url) {
    const theRequest = {};
    if (url.indexOf('?') !== -1) {
        const str = url.split('?')[1];
        const strs = str.split('&');
        for (let i = 0; i < strs.length; i += 1) {
            theRequest[strs[i].split('=')[0]] = decodeURI(strs[i].split('=')[1]);
        }
    }
    return theRequest;
}
