    var circle = `<div id="waite" style="height: 46px;width: 46px;margin: 200px 0 30px calc(50vw - 175px);">
            <svg viewBox="0 0 50 50">
                <circle cx="25" cy="25" r="20" fill="none" stroke="#3367d6" stroke-width="4" style="
                      -webkit-animation: ant-progress-loading 1.4s ease-in-out infinite;
                      animation: ant-progress-loading 1.4s ease-in-out infinite;
                      stroke-dasharray: 80px 200px;
                      stroke-dashoffset: 0;
                      "></circle>
            </svg>
    </div>`;
    $("#collect-search-input").bind('input propertychange', function () {
        var search_val = $.trim($("#collect-search-input").val());
        var collects = document.getElementsByClassName("item-block");
        for (var i = 0; i < collects.length; i++) {
            var val = collects[i].title;
            var flag = false;
            if (val.toLowerCase().indexOf(search_val.toLowerCase()) != -1) {
                flag = true
                collects[i].style.display = 'block';
            } else {
                collects[i].style.display = 'none';
            }
        }
    });

    $(document).on('click', '.item-actions', function (e) {
        var curruser = '$xcontext.getUser()';
        var currpage = e.currentTarget.title;
        var d = {
            "user": curruser,
            "page": currpage
        }
        options = {
            type: "POST",
            contentType: "application/json; charset=utf-8",
            data: JSON.stringify(d),
            dataType: "json",
            url: "/collect/page?type=delete",
            success: function (data) {
                if (data == "1") {
                    window.location.reload();
                }
            },
            error: function (err) {
                require(['toastMsg'], function (toastMsg) {
                    toastMsg.toast('删除失败！');
                })
            }
        };

        $.ajax(options);
    });


    var mainCollect = document.getElementById("main-collect");
    if (mainCollect) {
