function handleEditClick() {
    var folderName = getParams(location.search).folderName;
    var ele = jQuery("#edit-collect-pop-form input[name=collect_name]");
    ele.parent().addClass("ant-input-has-value");
    var maxlength=ele.attr('maxlength');
    ele.next('.ant-input-icon').find('.ant-input-length-info').text(folderName.length+'/'+maxlength);
    ele.attr("value", folderName);
    openModal("edit-collect-pop")
}
function handleFolderDeleteClick() {
    var curruser = '$xcontext.getUser()';
    var da = {
        "user": curruser,
        "id": getParams(location.search).folderId
    }
    options = {
        type: "POST",
        contentType: "application/json; charset=utf-8",
        data: JSON.stringify(da),
        dataType: "json",
        url: "/collect/page?folderType=delete",
        success: function (data) {
            if (data == "1") {
                window.location.href = `/bin/view/Main/收藏夹/?folderId=0&folderName=我的收藏`;
                closeModal("delete-collect-pop")
            } else if (data == "2") {
                require(['toastMsg'], function (toastMsg) {
                    toastMsg.toast('收藏夹内有数据，不可删除！');
                })
            } else {
                closeModal("delete-collect-pop")
            }
        },
        error: function (err) {
            require(['toastMsg'], function (toastMsg) {
                toastMsg.toast('删除失败！');
            })
        }
    };
    jQuery.ajax(options);
}

